import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "Isj",
      realName: "",
      avatar: "",
      location: "",
      repos: "",
      followers: "",
      url: "",
      notFound: ""
    };
  }
  render() {
    return (
      <div>
        <SearchBox fetchUser={this.fetchUser.bind(this)} />
        <Card data={this.state} />
      </div>
    );
  }
  //The api request function
  fetchApi(url) {
    fetch(url)
      .then(res => res.json())
      .then(data => {
        //Update status with API data
        this.setState({
          username: data.login,
          realName: data.name,
          avatar: data.avatar_url,
          location: data.location,
          repos: data.public_repos,
          followers: data.followers,
          url: data.html_url,
          notfound: data.message
        });
      })
      .catch(err => console.log("oh no!" + err));
  }
  fetchUser(username) {
    let url = "https://api.github.com/users/${username}";
    this.fetchApi(url);
  }
  componentDidMount() {
    let url = `https://api.github.com/users/${this.state.username}`;
    this.fetchApi(url);
  }
}

class SearchBox extends Component {
  handleClick(e) {
    e.preventDefault();
    let username = this.refs.search.getDOMNode().value;
    //sending the username value to parent component to fetch new data from API
    this.props.fetchUser(username);
    this.refs.search.getDOMNode().value = "";
  }
  render() {
    return (
      <form className="searchbox" onSubmit={this.handleClick.bind(this)}>
        <input
          ref="search"
          className="searchbox_input"
          type="text"
          placeholder="Enter the Username"
        />
        <input
          type="submit"
          className="searchbox_button"
          value="Click to search user"
        />
      </form>
    );
  }
}

class Card extends Component {
  render() {
    return (
      <div className="card">
        <a href={data.url} target="blank">
          <img className="card_avatar" src={data.avatar} />
        </a>
      </div>
    );
  }
}

export default App;
